import React from "react";
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import PropTypes from "prop-types";

import Header from "../../components/header/header.jsx";
import HeaderBanner from "../../components/banner/banner.jsx";
import Footer from "../../components/footer/footer.jsx";
import plainBanner from "../../components/header/header.jsx";
const Lesson1 = () => {
    return (
        <div id="main-wrapper">
            <Header />
            <div className="page-wrapper">
                <div className="container-fluid">
                    <HeaderBanner />
                    <h1 className="title">Lesson 1: Introduction to the World of Robotics</h1>

                    {/* Lesson Content */}
                    <div className="lesson-content">
                        <h2>Course Overview</h2>
                        <p>
                            Embark on your robotics programming journey with this foundational course on ROS basics.
                            Gain the skills necessary to program robots and understand the core concepts of ROS in just three lessons.
                        </p>

                        <h2>What You Will Learn</h2>
                        <ul>
                            <li>Introduction to Robotics and Real-World Applications</li>
                            <li>ROS Architecture and Workspace Setup</li>
                            <li>Writing a Simple ROS Program for Robot Movement</li>
                        </ul>

                        <h2>Lesson 1: Introduction to Robotics and Real-World Applications</h2>
                        <h3>Description:</h3>
                        <p>
                            Explore the world of robotics and its applications in various industries.
                            Discuss the impact of robotics on society and the evolving role of robots in different fields.
                        </p>

                        <h3>Learning Objectives:</h3>
                        <ul>
                            <li>Define key concepts in robotics.</li>
                            <li>Understand real-world applications of robotics.</li>
                            <li>Recognize the societal impact of robotics.</li>
                        </ul>

                        <h3>Simulations, Websites, and Resources:</h3>
                        <ul>
                            <li>Videos showcasing robotic applications.</li>
                            <li>Online articles on current robotic advancements.</li>
                            <li>Robotics textbooks for further reading.</li>
                        </ul>
                    </div>

                    {/* End of Lesson Content */}
                </div>
            </div>
            <Footer />
        </div>
    );
}

Lesson1.propTypes = {
    classes: PropTypes.object
};

export default Lesson1;
